head.script:
    var gubun;
	scwin.onpageload = function() {
		var urcEntpPpNo = com.data.getParameter("urcEntpPpNo");
		dma_urcEntpPpNo.set("urcEntpPpNo", urcEntpPpNo);

		var urcEntpSvcDivsCd = com.data.getParameter("urcEntpSvcDivsCd");
		dma_prodDiv.set("urcEntpSvcDivsCd", urcEntpSvcDivsCd );

		scwin.setCompEpos(com.data.getParameter("urcEntpSvcCd"));
		if ( dma_urcEntpPpNo.get("urcEntpPpNo")!= "") {
			gubun = "retrieve";
			sbx_entpSvcDivsCd.setDisabled(true);
			sbx_entpSvcCd.setDisabled(true);
			sbx_ppCd.setDisabled(true);
		} else {
			gubun = "reg";
			grp_regInfo.hide();
			scwin.setOffrList();
		}
	};

	scwin.onpageunload = function() {

	};

	/* 아이콘 pc이미지 업로드 */
	scwin.btn_iconPcImgUpd_onclick = function(e) {
		ecUtil.loadFilePopup("scwin.popupCallBackIconPcUpdImg", "uhdc/entp/pr", "public-img");
	};
	scwin.popupCallBackIconPcUpdImg = function(retObj){
		if (retObj.status == "S") {
			ecUtil.toast("com.inf.0008");
			console.log('#retObj : ', retObj);
			console.log('#retObj : ', retObj.uiUrl);
			dma_snglPdInfo.set("pcIconImgeUrl", retObj.uiUrl);
			console.log('#dma_snglPdInfo.pcIconImgeUrl : ', dma_snglPdInfo.get("pcIconImgeUrl"));
		} else {
			ecUtil.alert(["com.alt.0004", retObj.fileOrginName]);
		}
	};

	/* 아이콘모바일이미지 업로드 */
	scwin.btn_iconMblImgUpd_onclick = function(e) {
		ecUtil.loadFilePopup("scwin.popupCallBackIconMblUpdImg", "uhdc/entp/pr", "public-img");
	};
	scwin.popupCallBackIconMblUpdImg = function(retObj){
		if (retObj.status == "S") {
			ecUtil.toast("com.inf.0008");
			console.log('#retObj : ', retObj);
			console.log('#retObj : ', retObj.uiUrl);
			dma_snglPdInfo.set("mblIconImgeUrl", retObj.uiUrl);
			console.log('#dma_snglPdInfo.mblIconImgeUrl : ', dma_snglPdInfo.get("mblIconImgeUrl"));
		} else {
			ecUtil.alert(["com.alt.0004", retObj.fileOrginName]);
		}
	};

	/* 아이콘 pc이미지 미리보기 버튼 클릭 시 */
	scwin.btn_prvPcIconImge_onclick = function(e) {
		var src = dma_snglPdInfo.get("pcIconImgeUrl");
		scwin.previewImg("scwin.popupCallBackPreview" , src);
	};

	scwin.previewImg = function(callbackFunc , src){
		var url = "/web/uhdc/cmm/cmmPreImagePopup.xml";
		var imgDomain = com.win.getUserLoginInfo().imageDomain;
		var imgSrc = imgDomain + src;

		var data = {
				src : imgSrc,
				callbackFn : callbackFunc
				};
		var options = { id : "previewPcImg",
					popupName : "이미지 미리보기",
					modal : true,
					width : 802,
					height: 402,
					type : "wframePopup"
					};
		com.win.openPopup(url, options, data);
	};
	scwin.popupCallBackPreview = function(retObj){}

	/* 아이콘 모바일이미지 미리보기 버튼 클릭 시 */
	scwin.btn_prvMblIconImge_onclick = function(e) {
		var src = dma_snglPdInfo.get("mblIconImgeUrl");
		scwin.previewImg("scwin.popupCallBackPreview" , src);
	};

	scwin.setCompEpos = function(svcCd){
		if(svcCd == "TV"){
			grp_normTable.hide();
			grp_cctvTable.hide();
			grp_tvTable.show("");
		}else if(svcCd == "CT"){
			grp_normTable.hide();
			grp_cctvTable.show("");
			grp_tvTable.hide();
			gen_cctvList.insertChild(0);
		}else{
			grp_normTable.show("");
			grp_cctvTable.hide();
			grp_tvTable.hide();
		}
	}
	scwin.sbm_retrieveProdList_submitdone = function(e) {
		var urcEntpSvcCd = com.data.getParameter("urcEntpSvcCd")
		if(urcEntpSvcCd!=""){
			sbx_entpSvcCd.setValue( urcEntpSvcCd );
		}
	};


	scwin.sbx_entpSvcDivsCd_onchange = function() {
		if(sbx_entpSvcDivsCd.getValue()!=""){
			com.sbm.execute(sbm_retrieveProdList, {}, gcm.SERVICE_LIST.SHEC);
		}else{
			dlt_prodList.removeAll();
		}
	};

	scwin.sbx_entpSvcCd_onchange = function() {
		dma_ppParam.set( "urcEntpSvcCd" , this.getValue());
		com.sbm.execute(sbm_retrievePpList, {}, gcm.SERVICE_LIST.SHEC);	

		scwin.setCompEpos(this.getValue())
	};

	scwin.sbm_retrieveSnglProd_submitdone = function(e) {
		var entpSnglProdOffr = dma_snglPdInfo.get("entpSnglProdOffr");
		dlt_snglProdOffr.setJSON(entpSnglProdOffr);

		var entpPpCctvAgmt = dma_snglPdInfo.get("entpPpCctvAgmt");
		dlt_ppCctvAgmt.setJSON(entpPpCctvAgmt);

		scwin.setOffrList();
		scwin.setCctvAgmt();
	};

	scwin.setOffrList = function(){
		if(dlt_snglProdOffr.getTotalRow()>0){
			var offrList = dlt_snglProdOffr.getAllJSON();
			for(var i in offrList){
				i=Number(i);
				var offrComp = com.util.getComponent("grp_offrNm0"+Number(i+1));
				offrComp.getParent().show("");
				offrComp.show("");
				offrComp.getChildren()[0].setValue(offrList[i].urcEntpOffrNm)

			}
			if(dlt_snglProdOffr.getTotalRow()!=3){
				for(var i=2; i>=dlt_snglProdOffr.getTotalRow(); i--){
					var offrComp = com.util.getComponent("grp_offrNm0"+Number(i+1));
					offrComp.getParent().hide();
				}
			}
		}else{
			for(var i = 0; i<3; i++){
				var offrComp = com.util.getComponent("grp_offrNm0"+Number(i+1));
				offrComp.getParent().hide();
			}
		}
	}

	scwin.setCctvAgmt = function(){
		gen_cctvList.removeAll();
		var cctvCnt = dma_snglPdInfo.get("cctvRqstCnt");
		if(cctvCnt !="" && cctvCnt !=9 ){
			for(var i =0 ; i<cctvCnt ; i++){
				gen_cctvList.insertChild(i);
				gen_cctvList.getChild( i , "tbx_cctvCnt" ).setValue(i+1+"대");

				gen_cctvList.getChild( i , "inp_yy3AgmtChrgCctv").setValue(dlt_ppCctvAgmt.getCellData(i,"cctvYy3AgmtChrg"));
				gen_cctvList.getChild( i , "inp_yy2AgmtChrgCctv").setValue(dlt_ppCctvAgmt.getCellData(i,"cctvYy2AgmtChrg"));
				gen_cctvList.getChild( i , "inp_yy1AgmtChrgCctv").setValue(dlt_ppCctvAgmt.getCellData(i,"cctvYy1AgmtChrg"));
				gen_cctvList.getChild( i , "inp_nagmChrgCctv").setValue(dlt_ppCctvAgmt.getCellData(i,"cctvNagmChrg"));
			}
		}else if(cctvCnt == 9){
			gen_cctvList.insertChild(0);
			gen_cctvList.getChild( 0 , "tbx_cctvCnt" ).setValue("9대 이상");

			gen_cctvList.getChild( 0 , "inp_yy3AgmtChrgCctv").setValue(dlt_ppCctvAgmt.getCellData(0,"cctvYy3AgmtChrg"));
			gen_cctvList.getChild( 0 , "inp_yy2AgmtChrgCctv").setValue(dlt_ppCctvAgmt.getCellData(0,"cctvYy2AgmtChrg"));
			gen_cctvList.getChild( 0 , "inp_yy1AgmtChrgCctv").setValue(dlt_ppCctvAgmt.getCellData(0,"cctvYy1AgmtChrg"));
			gen_cctvList.getChild( 0 , "inp_nagmChrgCctv").setValue(dlt_ppCctvAgmt.getCellData(0,"cctvNagmChrg"));
		}
	}
	scwin.btn_cnt_onclick = function(e) {
		var tabIndex = com.data.getParameter("tabIndex");
		scwin.moveList(tabIndex);
	};

	scwin.moveList = function(tabIndex) {
		pScwinObj = com.win.getParentFrame().getObj("scwin");
		pScwinObj.closeTab(tabIndex);
		//
	};

	scwin.sbm_retrievePpList_submitdone = function(e) {
		if(sbx_ppCd.getValue() == "" && gubun != "retrieve"  ){
			return;
		}
		com.sbm.execute(sbm_retrieveSnglProd, {}, gcm.SERVICE_LIST.SHEC);
	};


	scwin.sbx_ppCd_onchange = function() {
		var matchJson = dlt_ppList.getMatchedJSON( "urcEntpPpNo" , this.getValue() , true , 0 , dlt_ppList.getTotalRow());
		if(matchJson.length>0 && matchJson[0].snglPpProdUseYn=="Y" 
			&& gubun !="retrieve" && com.data.getParameter("urcEntpPpNo") != this.getValue()){
			sbx_ppCd.setValue("");
			com.win.alert("이미 등록된 단독상품 입니다.");
		}else{
			if(gubun =="retrieve"){
				gubun = "update";
			}
			if(sbx_ppCd.getValue()==""){
				tbx_ppCd.setValue("");
				return;
			}
			dma_urcEntpPpNo.set( "urcEntpPpNo" ,  this.getValue()  );
			tbx_ppCd.setValue(matchJson[0].ppCd);
			com.sbm.execute(sbm_retrieveSnglProd, {}, gcm.SERVICE_LIST.SHEC);
		}

	};

	scwin.fn_validChk = function(){
		var json = dma_snglPdInfo.getJSON();
		dma_insertSnglPpInfo.setJSON(json);

		if(sbx_entpSvcCd.getValue() == ""){
			com.win.alert("상품 구분은 필수 값 입니다.");
			return false;
		}
		if(dma_insertSnglPpInfo.get("urcEntpPpNo") == ""){
			com.win.alert("요금제는 필수 값 입니다.");
			return false;
		}

		if (!com.data.validateGroup(grp_detl)) return false;

		return true;
	}

	scwin.btn_save_onclick = function(e) {
		if(!scwin.fn_validChk()) return;

		for(var i = 0; i<dlt_snglProdOffr.getTotalRow(); i++){
			dlt_snglProdOffr.setCellData( i , "offrAplyPiorRank" , i+1 );
		}

		dma_insertSnglPpInfo.set( "entpSnglProdOffr" , " " );
		dma_insertSnglPpInfo.set( "entpSnglProdOffr" , dlt_snglProdOffr.getAllJSON() );
		dma_insertSnglPpInfo.set("urcDcntBnftPpCntn", dma_snglPdInfo.get("urcDcntBnftPpCntn"));
		dma_insertSnglPpInfo.set("pcIconImgeUrl", dma_snglPdInfo.get("pcIconImgeUrl"));
		dma_insertSnglPpInfo.set("mblIconImgeUrl", dma_snglPdInfo.get("mblIconImgeUrl"));
		dma_insertSnglPpInfo.set("entpPcImgeAltrTxtCntn", dma_snglPdInfo.get("entpPcImgeAltrTxtCntn"));
		dma_insertSnglPpInfo.set("entpMblImgeAltrTxtCntn", dma_snglPdInfo.get("entpMblImgeAltrTxtCntn"));
		dma_insertSnglPpInfo.set("ppbDscrEposYn", dma_snglPdInfo.get("ppbDscrEposYn"));
		dma_insertSnglPpInfo.set("pcPpDscr", dma_snglPdInfo.get("pcPpDscr"));
		dma_insertSnglPpInfo.set("mblPpDscr", dma_snglPdInfo.get("mblPpDscr"));

		console.log('#dma_insertSnglPpInfo : ', dma_insertSnglPpInfo);

		if(gubun == "update"){
			com.win.confirm(com.data.getMessage("com.cfm.0001"), function(e){
			if(e.clickValue){
				com.sbm.execute(sbm_updateSnglPp, {}, gcm.SERVICE_LIST.SHEC);
			}});
		}else{
			com.win.confirm(com.data.getMessage("com.cfm.0001"), function(e){
			if(e.clickValue){
				com.sbm.execute(sbm_insertSnglPp, {}, gcm.SERVICE_LIST.SHEC);
			}});

		}
	};

	scwin.btnOffr_onclick = function(e) {

		var data = {
			data : {},
			callbackFn : "scwin.btnOffr_PopupCallBack"
		};
		var options = {
			id : "ShowOffrSelect",
			popupName : "오퍼(프로모션) ",
			modal : true,
			width : 1000,
			height : 600
		};

		com.win.openPopup("/web/uhdc/smem/of/smemOfPmtnPopu.xml", options, data);

	};
	scwin.btnOffr_PopupCallBack = function(e) {
		dlt_snglProdOffr.removeAll();

		dlt_snglProdOffr.setJSON(e.data);
		dlt_snglProdOffr.sort("offrAplyPiorRank" , 0);
		scwin.setOffrList();
	};

	scwin.btn_offrDel_onclick = function(e) {
		var rowNum = Number(this.org_id.substring(this.org_id.length-1));

		var offrComp = com.util.getComponent(this.org_id);
		offrComp.getParent().hide();
		dlt_snglProdOffr.removeRow(rowNum-1);

	};

	scwin.sbm_updateSnglPp_submitdone = function(e) {
		com.win.toast(com.data.getMessage("com.inf.0006"));
		var tabIndex = com.data.getParameter("tabIndex");
		scwin.moveList(tabIndex);
	};

	scwin.sbm_insertSnglPp_submitdone = function(e) {
		com.win.toast(com.data.getMessage("com.inf.0006"));
		var tabIndex = com.data.getParameter("tabIndex");
		scwin.moveList(tabIndex);
	};
